mapper.select:
/** * Query ID : selectSvrList * Description : Repository 등록된 DB의 서버 리스트 조회 * Author : 변승우 **/
SELECT A.DB_SVR_ID , A.DB_SVR_NM , C.IPADR , C.PORTNO , C.MASTER_GBN FROM T_DBSVR_I A, T_DB_I B, T_DBSVRIPADR_I C WHERE 1 = 1 AND A.DB_SVR_ID = B.DB_SVR_ID AND A.DB_SVR_ID = C.DB_SVR_ID AND C.MASTER_GBN = 'M'
AND A.DB_SVR_ID = #{value}
AND A.USEYN = 'Y' AND B.USEYN = 'Y' GROUP BY A.DB_SVR_NM, A.DB_SVR_ID, C.IPADR, C.PORTNO, C.MASTER_GBN

mapper.update:
   	    /**
	 	* Query ID : updateDB
	 	* Description : DB 수정
	 	* Author : 변승우
	 	**/
	 	
		UPDATE T_DB_I
		SET 
	  	 	   USEYN= #{useyn}
	  	 	  , DB_EXP=#{db_exp}
			  , LST_MDFR_ID=#{lst_mdfr_id}
			  , LST_MDF_DTM=clock_timestamp()	
		WHERE DB_SVR_ID=#{db_svr_id}::numeric
		AND DB_NM=#{dft_db_nm}

