  nextval('q_db2pg_ddl_wrk_inf_01')
, #{wrk_id}
, #{db2pg_sys_id}
, #{db2pg_uchr_lchr_val}
, #{src_tb_ddl_exrt_tf}
, #{ddl_save_pth}

, #{db2pg_exrt_trg_tb_wrk_id}


