mapper.select:
/** * Query ID : selectScaleCngList * Description : scale Auto 설정 list 조회 * Author : **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM , (ROW_NUMBER() OVER()) AS IDX , AA.WRK_ID , AA.DB_SVR_ID , AA.SCALE_TYPE , AA.SCALE_TYPE_NM , AA.POLICY_TYPE , AA.POLICY_TYPE_NM , AA.AUTO_POLICY_SET_DIV , AA.AUTO_POLICY_TIME , AA.AUTO_LEVEL , AA.EXECUTE_TYPE , AA.EXECUTE_TYPE_NM , AA.EXPANSION_CLUSTERS , AA.MIN_CLUSTERS , AA.MAX_CLUSTERS , AA.FRST_REGR_ID , AA.FRST_REG_DTM , AA.LST_MDFR_ID , AA.LST_MDF_DTM , AA.USEYN FROM ( SELECT A.WRK_ID AS WRK_ID , B.DB_SVR_ID AS DB_SVR_ID , A.SCALE_TYPE AS SCALE_TYPE , (CASE WHEN A.SCALE_TYPE = '1' THEN 'Scale-in' ELSE 'Scale-out' END) AS SCALE_TYPE_NM , A.POLICY_TYPE AS POLICY_TYPE , (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.POLICY_TYPE = SYS_CD ) AS POLICY_TYPE_NM , A.AUTO_POLICY_SET_DIV AS AUTO_POLICY_SET_DIV , A.AUTO_POLICY_TIME AS AUTO_POLICY_TIME , A.AUTO_LEVEL AS AUTO_LEVEL , A.EXECUTE_TYPE AS EXECUTE_TYPE , (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.EXECUTE_TYPE = SYS_CD ) AS EXECUTE_TYPE_NM , A.EXPANSION_CLUSTERS AS EXPANSION_CLUSTERS , A.MIN_CLUSTERS AS MIN_CLUSTERS , A.MAX_CLUSTERS AS MAX_CLUSTERS , A.FRST_REGR_ID AS FRST_REGR_ID , TO_CHAR(A.FRST_REG_DTM,'YYYY-MM-DD HH24:MI') AS FRST_REG_DTM , A.LST_MDFR_ID AS LST_MDFR_ID , TO_CHAR(A.LST_MDF_DTM,'YYYY-MM-DD HH24:MI') AS LST_MDF_DTM , A.USEYN AS USEYN FROM T_SCALE_I A JOIN T_DBSVRIPADR_I B ON A.DB_SVR_IPADR_ID = B.DB_SVR_IPADR_ID AND B.MASTER_GBN = 'M' WHERE B.DB_SVR_ID=#{db_svr_id}::numeric
AND A.SCALE_TYPE=#{scale_type_cd}
AND A.POLICY_TYPE = #{policy_type_cd}
AND A.EXECUTE_TYPE = #{execute_type_cd}
AND A.WRK_ID = #{wrk_id}::numeric
ORDER BY A.LST_MDF_DTM DESC )AA

/** * Query ID : selectScaleLog * Description : SCALE Load 조회 * Author : **/ SELECT A.WRK_ID , A.FRST_REGR_ID , A.EXE_RSLT_CD , A.SCALE_TYPE FROM T_SCALE_G A WHERE 1 = 1
AND A.SCALE_WRK_SN = (SELECT SCALE_WRK_SN FROM T_SCALE_G WHERE 1= 1 AND PROCESS_ID = #{instance_id} AND SCALE_TYPE IN ('1', '2') )
AND A.SCALE_WRK_SN = (SELECT MAX(SCALE_WRK_SN) FROM T_SCALE_G WHERE EXE_RSLT_CD = 'TC001701' AND SCALE_TYPE IN ('1', '2') )

/**
* Query ID : selectAutoScaleSetChk
* Description : Auto scale 설정 중복 체크
* Author : 
**/
SELECT COUNT(*)
  FROM T_SCALE_I 
  WHERE DB_SVR_ID = #{db_svr_id}::numeric
    AND SCALE_TYPE = #{scale_type_cd}
    AND POLICY_TYPE = #{policy_type_cd}
    AND EXECUTE_TYPE = #{execute_type_cd}
    AND AUTO_POLICY_SET_DIV = #{auto_policy_set_div}
    AND USEYN = #{useyn}::bpchar
    AND AUTO_LEVEL = ${auto_level}
    AND AUTO_POLICY_TIME = ${auto_policy_time}::numeric


     AND WRK_ID != #{wrk_id}::numeric

mapper.update:
/**
* Query ID : "updateAutoScaleSetting"
* Description : Auto scale 설정 수정
* Author : 
**/
UPDATE T_SCALE_I
   SET SCALE_TYPE = #{scale_type_cd}
     , POLICY_TYPE = #{policy_type_cd}
     , AUTO_POLICY_SET_DIV = #{auto_policy_set_div}
     , AUTO_POLICY_TIME = #{auto_policy_time}::numeric
     , AUTO_LEVEL = #{auto_level}
     , EXECUTE_TYPE = #{execute_type_cd}
     , EXPANSION_CLUSTERS = #{expansion_clusters}::numeric
     , MIN_CLUSTERS = #{min_clusters}::numeric
     , MAX_CLUSTERS = #{max_clusters}::numeric
     , LST_MDFR_ID = #{login_id}
     , LST_MDF_DTM = clock_timestamp()
     , USEYN = #{useyn}::bpchar
 WHERE DB_SVR_ID = #{db_svr_id}::numeric
   AND WRK_ID = #{wrk_id}::numeric

mapper.select:
/** * Query ID : selectScaleHistoryList * Description : scale 실행이력 조회 * Author : **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM, AA.DB_SVR_ID, AA.IPADR, AA.PROCESS_ID, AA.SCALE_WRK_SN, AA.WRK_ID, AA.SCALE_TYPE, AA.SCALE_TYPE_NM, AA.WRK_TYPE, AA.WRK_TYPE_NM, AA.AUTO_POLICY, AA.POLICY_TYPE_NM,
AA.AUTO_POLICY_SET_DIV, AA.AUTO_POLICY_TIME, AA.AUTO_LEVEL,
AA.CLUSTERS, AA.WRK_STRT_DTM, AA.WRK_END_DTM, AA.WRK_DTM, AA.RSLT_MSG, AA.EXE_RSLT_CD, AA.EXE_RSLT_CD_NM, AA.FRST_REGR_ID, AA.FRST_REG_DTM, AA.LST_MDFR_ID, AA.LST_MDF_DTM FROM ( SELECT B.DB_SVR_ID AS DB_SVR_ID, B.IPADR AS IPADR, A.PROCESS_ID AS PROCESS_ID, A.SCALE_WRK_SN AS SCALE_WRK_SN, A.WRK_ID AS WRK_ID, A.SCALE_TYPE AS SCALE_TYPE, (CASE WHEN A.SCALE_TYPE = '1' THEN 'Scale-in' ELSE 'Scale-out' END) AS SCALE_TYPE_NM, A.WRK_TYPE AS WRK_TYPE, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.WRK_TYPE = SYS_CD ) AS WRK_TYPE_NM, A.AUTO_POLICY AS AUTO_POLICY, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.AUTO_POLICY = SYS_CD ) AS POLICY_TYPE_NM,
A.AUTO_POLICY_SET_DIV, A.AUTO_POLICY_TIME, A.AUTO_LEVEL,
A.CLUSTERS AS CLUSTERS, TO_CHAR(A.WRK_STRT_DTM,'YYYY-MM-DD HH24:MI:SS') AS WRK_STRT_DTM, TO_CHAR(A.WRK_END_DTM,'YYYY-MM-DD HH24:MI:SS') AS WRK_END_DTM, replace(TO_CHAR(TO_CHAR(A.WRK_END_DTM,'YYYY-MM-DD HH24:MI:SS')::timestamp - TO_CHAR(A.WRK_STRT_DTM,'YYYY-MM-DD HH24:MI:SS')::timestamp, 'MI:SS'), '00:00', '00:01') AS WRK_DTM, A.RSLT_MSG AS RSLT_MSG, A.EXE_RSLT_CD AS EXE_RSLT_CD, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.EXE_RSLT_CD = SYS_CD ) AS EXE_RSLT_CD_NM, A.FRST_REGR_ID AS FRST_REGR_ID, TO_CHAR(A.FRST_REG_DTM,'YYYY-MM-DD HH24:MI') AS FRST_REG_DTM, A.LST_MDFR_ID AS LST_MDFR_ID, TO_CHAR(A.LST_MDF_DTM,'YYYY-MM-DD HH24:MI') AS LST_MDF_DTM FROM T_SCALE_G A JOIN T_DBSVRIPADR_I B ON A.DB_SVR_IPADR_ID = B.DB_SVR_IPADR_ID AND B.MASTER_GBN = 'M' WHERE B.DB_SVR_ID=#{db_svr_id}::numeric
AND ( TO_DATE(to_char(a.WRK_STRT_DTM,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN TO_DATE(#{wrk_strt_dtm},'YYYY-MM-DD') AND TO_DATE(#{wrk_end_dtm},'YYYY-MM-DD') OR TO_DATE(to_char(a.WRK_END_DTM,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN TO_DATE(#{wrk_strt_dtm},'YYYY-MM-DD') AND TO_DATE(#{wrk_end_dtm},'YYYY-MM-DD') )
AND A.EXE_RSLT_CD=#{exe_rslt_cd}
AND A.SCALE_TYPE=#{scale_type_cd}
AND A.PROCESS_ID LIKE #{process_id_set} || '%'
AND A.WRK_TYPE = #{wrk_type_Cd}
ORDER BY A.PROCESS_ID DESC )AA

/** * Query ID : selectScaleOccurHistoryList * Description : scale 발생이력 조회 * Author : **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM, AA.DB_SVR_ID, AA.IPADR, AA.WRK_SN, AA.SCALE_TYPE, AA.SCALE_TYPE_NM, AA.POLICY_TYPE, AA.POLICY_TYPE_NM, AA.AUTO_POLICY_SET_DIV, AA.AUTO_POLICY_TIME, AA.AUTO_LEVEL, AA.EXECUTE_TYPE, AA.EXECUTE_TYPE_NM, AA.EVENT_OCCUR_CONTENTS, AA.EVENT_OCCUR_DTM, AA.FRST_REGR_ID, AA.FRST_REG_DTM, AA.LST_MDFR_ID, AA.LST_MDF_DTM FROM ( SELECT B.DB_SVR_ID AS DB_SVR_ID, B.IPADR AS IPADR, A.WRK_SN AS WRK_SN, A.SCALE_TYPE AS SCALE_TYPE, (CASE WHEN A.SCALE_TYPE = '1' THEN 'Scale-in' ELSE 'Scale-out' END) AS SCALE_TYPE_NM, A.POLICY_TYPE AS POLICY_TYPE, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.POLICY_TYPE = SYS_CD ) AS POLICY_TYPE_NM, A.AUTO_POLICY_SET_DIV AS AUTO_POLICY_SET_DIV, A.AUTO_POLICY_TIME AS AUTO_POLICY_TIME, A.AUTO_LEVEL AS AUTO_LEVEL, A.EXECUTE_TYPE AS EXECUTE_TYPE, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.EXECUTE_TYPE = SYS_CD ) AS EXECUTE_TYPE_NM, A.EVENT_OCCUR_CONTENTS AS EVENT_OCCUR_CONTENTS, TO_CHAR(A.EVENT_OCCUR_DTM,'YYYY-MM-DD HH24:MI') AS EVENT_OCCUR_DTM, A.FRST_REGR_ID AS FRST_REGR_ID, TO_CHAR(A.FRST_REG_DTM,'YYYY-MM-DD HH24:MI') AS FRST_REG_DTM, A.LST_MDFR_ID AS LST_MDFR_ID, TO_CHAR(A.LST_MDF_DTM,'YYYY-MM-DD HH24:MI') AS LST_MDF_DTM FROM T_SCALEOCCUR_G A JOIN T_DBSVRIPADR_I B ON A.DB_SVR_IPADR_ID = B.DB_SVR_IPADR_ID AND B.MASTER_GBN = 'M' WHERE B.DB_SVR_ID=#{db_svr_id}::numeric
AND TO_DATE(to_char(A.EVENT_OCCUR_DTM,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN TO_DATE(#{wrk_strt_dtm},'YYYY-MM-DD') AND TO_DATE(#{wrk_end_dtm},'YYYY-MM-DD')
AND A.SCALE_TYPE=#{scale_type_cd}
AND A.POLICY_TYPE = #{policy_type_cd}
AND A.EXECUTE_TYPE = #{execute_type_cd}
ORDER BY A.EVENT_OCCUR_DTM DESC )AA

/** * Query ID : selectScaleWrkInfo * Description : scale wrk 정보 조회 * Author : **/ SELECT A.SCALE_WRK_SN AS SCALE_WRK_SN, A.WRK_ID AS WRK_ID, A.SCALE_TYPE AS SCALE_TYPE, (CASE WHEN A.SCALE_TYPE = '1' THEN 'Scale-in' ELSE 'Scale-out' END) AS SCALE_TYPE_NM, B.DB_SVR_ID AS DB_SVR_ID, B.IPADR AS IPADR, A.WRK_TYPE AS WRK_TYPE, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.WRK_TYPE = SYS_CD ) AS WRK_TYPE_NM, A.AUTO_POLICY AS AUTO_POLICY, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.AUTO_POLICY = SYS_CD ) AS POLICY_TYPE_NM,
A.AUTO_POLICY_SET_DIV, A.AUTO_POLICY_TIME, A.AUTO_LEVEL,
A.CLUSTERS AS CLUSTERS, A.PROCESS_ID AS PROCESS_ID, TO_CHAR(A.WRK_STRT_DTM,'YYYY-MM-DD HH24:MI:SS') AS WRK_STRT_DTM, TO_CHAR(A.WRK_END_DTM,'YYYY-MM-DD HH24:MI:SS') AS WRK_END_DTM, replace(TO_CHAR(TO_CHAR(A.WRK_END_DTM,'YYYY-MM-DD HH24:MI:SS')::timestamp - TO_CHAR(A.WRK_STRT_DTM,'YYYY-MM-DD HH24:MI:SS')::timestamp, 'MI:SS'), '00:00', '00:01') AS WRK_DTM, A.EXE_RSLT_CD AS EXE_RSLT_CD, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.EXE_RSLT_CD = SYS_CD ) AS EXE_RSLT_CD_NM, A.RSLT_MSG AS RSLT_MSG, A.FRST_REGR_ID AS FRST_REGR_ID, TO_CHAR(A.FRST_REG_DTM,'YYYY-MM-DD HH24:MI') AS FRST_REG_DTM, A.LST_MDFR_ID AS LST_MDFR_ID, TO_CHAR(A.LST_MDF_DTM,'YYYY-MM-DD HH24:MI') AS LST_MDF_DTM FROM T_SCALE_G A JOIN T_DBSVRIPADR_I B ON A.DB_SVR_IPADR_ID = B.DB_SVR_IPADR_ID AND B.MASTER_GBN = 'M' WHERE B.DB_SVR_ID=#{db_svr_id}::numeric AND A.SCALE_WRK_SN = #{scale_wrk_sn}
